  return <h1>{props.course}</h1>;
};
const Content = (props) => {
  return (
    <div>
      {props.info.map((inf, i) => {
        return <Part inf={inf} index={i} />;
      })}
    </div>
  );
};
const Part = ({inf, index}) => {
  return (

      <div key={index}>

        <h1>{inf.part}</h1>
        <h3>{inf.exercises}</h3>
      </div>

  );
};
const Total = (props) => {
  return <p>Number of exercises {props.total}</p>;
};

